import React,{useState} from 'react';
import movies from '../api/movies';

const useMovies = () => {
    const [movies,setMovies]=useState([]);
    const loadMovies=async()=>{
        const response=await movies.get();
        setMovies(response.phim);
    }
    useEffect(()=>{
        loadMovies();
    },[])
    return movies;
}

export default useMovies;
