import React from 'react'

const MovieItem = ({movie}) => {
    return (
        <div className="item">
            <div className="image">
                <img src={movie.imageUrl}/>
            </div>
            <div className="content">
                <div className="header">
                    {movie.title}
                </div>
                <div className="meta">
                    {movie.category}
                </div>
            </div>
        </div>
    )
}

export default MovieItem
