import React,{useState} from 'react';

const category=[
    {category:'Phim bộ'},
    {category:'Phim hoạt hình'},
    {category:'Phim chiếu rap'},
    {category:'Phim lẻ'},
];
const DropDown = () => {
const[open,setOpen]=useState(false);
const renderedCategory=category.map(ca=>{
    return<div key={ca.category} className="item">{ca.category}</div>
})

    return (
      
        <div className="ui pointing dropdown link item">
            <div className="item">
                <div className="ui dropdown" >
                    <div className="text">Loại phim</div>
                    <i className="dropdown icon"></i>
                    <div className="menu visible transition">
                        {renderedCategory}
                    </div>
                </div>        
            </div>
        </div>
    )
}

export default DropDown
