import React from 'react';
import ListEpisode from '../ListEpisode/ListEpisode';
import './Style.css';

const MovieItem = ({movie}) => {
    console.log(movie.episode[0].episode);
    return (
        <div className="movie-item item ">
            <div className="ui image">
                {/* <img src={movie.imageUrl}/> */}
            </div>
            <div className="content">
                <div className="header">
                    {movie.title}
                </div>
                <div className="meta">
                    {movie.category}
                </div>
                <div className="extra">
                    {/* <ListEpisode episodeList={movie.episode}/> */}
                </div>
            </div>
        </div>
    )
}

export default MovieItem;
