import React,{useState} from 'react';

const categoryList=[
    {category:'Phim bộ'},
    {category:'Phim hoạt hình'},
    {category:'Phim chiếu rap'},
    {category:'Phim lẻ'},
];
const DropDown = () => {
const[open,setOpen]=useState(false);
const[category,setSelectCategory]=useState(null);

console.log(category)

const onChangCategory=(ca)=>{
    setSelectCategory(ca);
    setOpen(!open)
}
const renderedCategory=categoryList.map(ca=>{
    return<div key={ca.category} className="item" onClick={onChangCategory(ca)}>{ca.category}</div>
})

    return (
      
        <div className="ui pointing dropdown link item">
             <div className="text">Loại phim</div>
              <i className="dropdown icon"></i>
                <div className={`menu ${open?'visible transition':''}`}>
                    {renderedCategory}
                </div>     
        </div>
    )
}

export default DropDown
