import React, { useEffect, useState } from 'react';
import SlideItem from './SlideItem/SlideItem';
import './Style.css'


const SlideBar = ({listmovie}) => {
  const[index,setIndex]=useState(0);
  const[slideshow,setSlideShow]=useState(null);
  useEffect(()=>{
    const timeid=setTimeout(() => {
      if(index===listmovie.length-1){
        setIndex(0)
      }else{
        setIndex(index+1)
      }
      // if(listmovie[index]){
      //   setSlideItem(listmovie[index].movie)
      // }
      setSlideShow(listmovie[index].movie)
    }, 3000);
    return ()=>clearTimeout(timeid)
  })
      return (
        <div className="ui piled segment">
          <div className="ui divided list list-slide container">
              <SlideItem movie={index===0?listmovie[3].movie:listmovie[index-1].movie}/>
              <SlideItem movie={listmovie[index].movie}/>
              <SlideItem movie={index===3?listmovie[0].movie:listmovie[index+1].movie}/>
          </div>    
        </div>
      )
  }

export default SlideBar
