import React, { useState } from 'react';
import SlideItem from './SlideItem/SlideItem';
const SlideBar = ({listmovie}) => {
const[index,setIndex]=useState(0);

setTimeout(() => {
  setIndex(index+1);
  if(index===renderSideShow.length-1){
    setIndex(0)
  }
}, 2000);

const renderSideShow=listmovie?listmovie.map(movie=>{
  return <SlideItem movie={movie.movie}/>
}):null
    return (
      <div>
            {renderSideShow}
      </div>
    )
}

export default SlideBar
