import useMovies from './hooks/useMovies';
import ListMovies from './components/ListMovies/ListMovies';
import VideoDetail from './components/VideoDetail/VideoDetail';
import Loading from './components/Loading/Loading';
const App = () => {
    const movies=useMovies();
    const [movie,setSelectMovie]=useState();  
    const renderedLoading=movies?'':<Loading message="Đang tải phim!"/>
    return (         
            <div>
                {/* {renderedLoading}
                <div className="ui segment" >              
                    <VideoDetail movie={movie}/>
                    <ListMovies ListMovies={movies[0].phimbo} onMovieClick={setSelectMovie} />
                </div> */}
                <Loading message="Đang tải phim!"/>
            </div>

    )
}

export default App;
