  // });

  const renderImgMovie=listmovie.map(movie=>{
    return <SlideItem movie={movie.movie}/>
  });

      return (
        <section className="slider">
            <i className="angle left icon left-arrow"/>
            <i className="angle right icon right-arrow" onClick={nextSlide}/>
             {renderImgMovie}  
        </section>
      )
  }

export default SlideBar
